INSERT INTO `fr_document_chunk` (`id`, `doc_id`, `pid`, `chunk`, `chunk_no`, `char_count`, `keyword`, `create_time`, `update_time`, `status`, `is_deleted`) VALUES (3822534420205568, 566260766639001600, 0, '问：申请劳动仲裁期间，公司更换了法人，写被申请人的时候需要写新法\n人？\n答：在申请劳动仲裁时，如果被申请人变更了法定代表人，那么在提交仲\n裁申请时，应当填写现期法定代表人或者负责人。\n如在仲裁委受理案件后被申请人处法定代表人变更，申请人可向劳动争议\n仲裁委员会示意即可。\n问：公司在劳动仲裁过程中变更了法人，被申请人信息怎么写？\n答：在申请劳动仲裁时，如果被申请人变更了法定代表人，那么在提交仲\n裁申请时，应当填写现期法定代表人或者负责人。\n如在仲裁委受理案件后被申请人处法定代表人变更，申请人可向劳动争议\n仲裁委员会示意即可。\n问：劳动仲裁中公司法人更换了，被申请人信息应如何填写？\n答：在申请劳动仲裁时，如果被申请人变更了法定代表人，那么在提交仲\n裁申请时，应当填写现期法定代表人或者负责人。\n如在仲裁委受理案件后被申请人处法定代表人变更，申请人可向劳动争议\n仲裁委员会示意即可。', 101, 377, NULL, '2025-04-17 21:09:19', '2025-04-17 21:09:19', 1, 0);
