-- Table structure for fr_document_chunk_vid
-- ----------------------------
DROP TABLE IF EXISTS `fr_document_chunk_vid`;
CREATE TABLE `fr_document_chunk_vid` (
  `id` bigint NOT NULL COMMENT '主键ID',
  `doc_id` bigint NOT NULL COMMENT '文档ID',
  `chunk_id` bigint NOT NULL COMMENT '切片ID',
  `vids` varchar(2048) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '向量数据ID集合',
  `create_time` datetime NOT NULL COMMENT '上传时间',
  `update_time` datetime NOT NULL COMMENT '最后修改时间',
  `status` tinyint NOT NULL DEFAULT '1' COMMENT '业务状态 0: 禁用 1:启用',
  `is_deleted` tinyint NOT NULL COMMENT '是否删除 0: 否 1: 是',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='切片的向量ID';

